;; **
;;; # Kalman Smoothing
;;; 
;; **

;; @@
(ns kalman
  (:require [anglican.pgas :as pgas]
    		[gorilla-plot.core :as plot]
            [clojure.pprint :refer [pprint]]
            [clojure.core.matrix :as mat
             :refer [matrix shape identity-matrix mul mmul add sub div zero-vector inverse transpose]]
            :reload)
  (:use [anglib
         [state-space :only [LDS]]]
    	[anglican core emit runtime
         [state :only [get-predicts get-log-weight]]]))

(mat/set-current-implementation :vectorz)

(def +plot-colors+ ["#05A" "#A05" "#0A5" "#50A" "#A50" "#5A0"])
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;kalman/+plot-colors+</span>","value":"#'kalman/+plot-colors+"}
