;; **
;;; ### Calculate effective number of distinct samples
;; **

;; @@
(defn eff-sample-size
  [v-fn samples]
  (let [;; aggregate weight for each unique sampled valued
        log-ws (map (fn [[v samples]]
                      (reduce log-sum-exp
                        (map get-log-weight 
                             samples)))
				    (group-by v-fn samples))
        ;; subtract maximum
        log-max-w (reduce max log-ws)
        ws (map exp (map #(- % log-max-w) log-ws))]
    (/ (square (reduce + ws))
       (reduce + (map square ws)))))

(def pgas-ess
  (for [t (range +T+)]
    (eff-sample-size 
      (comp #(nth % t) :states get-predicts)
      pgas-samples)))

(def smc-ess
  (for [t (range +T+)]
    (eff-sample-size 
      (comp #(nth % t) :states get-predicts)
      smc-samples)))
