;; @@
;; Translations from Bishop [1] notation
;;
;; x	y	observation			
;; 0 	m0	init-mean
;; V0	V0	init-cov
;; C 	C 	obs-matrix
;; 	S	obs-cov    
;; A	A	trans-matrix
;; 	G	trans-cov
;;
;; [1] Bishop, Christopher M. Pattern recognition and machine learning. 
;; New York: Springer, 2006. Pages 638-641.

(defn kalman-forward
  [observations 
   obs-matrix obs-cov 
   trans-matrix trans-cov 
   init-mean init-cov]
  (let [;; set-up some shorthands
        m0 init-mean
        V0 init-cov
        C obs-matrix
        Ct (transpose C)
        S obs-cov
        A trans-matrix
        At (transpose A)
        G trans-cov
        I (apply identity-matrix (shape init-cov))]
    (let [;; foward messages for first point
          V0Ct (mmul V0 Ct)
          CV0Ct (mmul C V0Ct)
          ;; B 13.97: K1 = V0 Ct (C V0 Ct + S)^-1
          K1 (mmul V0Ct (inverse (add CV0Ct S)))
          y1 (first observations)
          ;; B 13.94: m1 = m0 + K1 (y1 - C m0)
  	      m1 (add m0 (mmul K1 (sub y1 (mmul C m0))))
          ;; B 13.95: V1 = (I - K1 C) V0
          V1 (mmul (sub I (mmul K1 C)) V0)
          ;; B 13.96: c1 = N(y1 | C m0, C V0 Ct + S)
          c1 (observe (mvn (mmul C m0) (add CV0Ct S)) y1)]
      (loop [observations (rest observations)
             ms [m1]
             Vs [V1]
             Ps []
             cs [c1]]
        (if-let [y (first observations)]
          (let [Am- (mmul A (peek ms))
                CAm- (mmul C Am-)
                ;; B 13.88: P- = A V- At + G
                P- (add (mmul A (mmul (peek Vs) At)) G)
                P-Ct (mmul P- Ct)
                CP-Ct (mmul C P-Ct)
                ;; B 13.92: K = P- Ct (C P- Ct + S)^-1
                K (mmul P-Ct (inverse (add CP-Ct S)))
                ;; B 13.89: m = A m- K (y - C A m-)
                m (add Am- (mmul K (sub y CAm-)))
                ;; B 13.90: V = (I - K C) P-
                V (mmul (sub I (mmul K C)) P-)
                ;; B 13.91: c = N(y | C A m-, C P- Ct + S)
                c (observe (mvn CAm- (add CP-Ct S)) y)]
            (recur (rest observations)
                   (conj ms m)
                   (conj Vs V)
                   (conj Ps P-)
                   (conj cs c)))
          [ms Vs Ps cs])))))

(defn kalman-backward
  [trans-matrix 
   ms Vs Ps]
  (let [;; set-up shorthands
        A trans-matrix
        At (transpose A)
        AmT (mmul A (peek ms))]
    (loop [ms (pop ms)
           Vs (pop Vs)
           Ps Ps
           mhs (list (peek ms))
           Vhs (list (peek Vs))]
      (if (empty? ms)
          [mhs Vhs]
          (let [;; B 13.102: J = V At (inv P)
                J (mmul (peek Vs) (mmul At (inverse (peek Ps))))
                ;; B 13.100: mh = m + J (mh+ - A mT)
                mh (add (peek ms) (mmul J (sub (peek mhs) AmT)))
                ;; B 13.101: Vh = V + J (Vh+ - P) Jt
                Vh (add (peek Vs) 
                        (mmul J 
                              (mmul (sub (peek Vhs) 
                                         (peek Ps)) 
                                    (transpose J))))]
            (recur (pop ms)
                   (pop Vs)
                   (pop Ps)
                   (conj mhs mh)
                   (conj Vhs Vh)))))))

(defn kalman-smoother
  [observations 
   obs-matrix obs-cov 
   trans-matrix trans-cov 
   init-mean init-cov]
  (->> (kalman-forward observations
                       obs-matrix obs-cov 
					   trans-matrix trans-cov 
					   init-mean init-cov)
       (take 3)
       (apply kalman-backward trans-matrix)
       (map matrix)))
